   , []);

  return (
    <div className="App">
      <button onClick={()=>{
        signOut(auth).then(() => {
          // Sign-out successful.
          browserStorage.removeItem(userInfoKey);
        }).catch((error) => {
          // An error happened.
        });
      }}>out</button>
    </div>
  );
}

export default App;
